// COLOR VARIABLES
$color-primary: #c69963;
$color-primary-dark: #b28451;

$color-secondary: #101d2c;

$color-grey-light-1: #f9f7f6;
$color-grey-light-2: #aaa;

$color-grey-dark-1: #54483a;
$color-grey-dark-2: #6d5d4b;

// FONT VARIABLES
$font-primary: "Nunito", sans-serif;
$font-display: "Josefin Sans", sans-serif;

/// RESPONSIVE BREAKPOINTS ///@at-root
$bp-largest: 75rem; // 1200px
$bp-big: 62rem; //982px
$bp-large: 52rem; // 823px
$bp-small: 50rem; // 774px
$bp-smallest: 31.25rem;

*,
*::before,
*::after {
    margin: 0;
    padding: 0;
    box-sizing: inherit;
}

html {
    box-sizing: border-box;
    font-size: 62.5%; //1 rem = 10px

    @media (max-width: $bp-largest) {
        font-size: 60%;
    }
}

body {
    font-family: $font-primary;
    color: $color-grey-dark-2;
    font-weight: 300;
    line-height: 1.6;
}

.container {
    display: grid;

    grid-template-rows: 80vh min-content 40vw repeat(3, min-content);
    grid-template-columns:
        [sidebar-start] 8rem [sidebar-end full-start] minmax(6rem, 1fr)
        [center-start] repeat(
            8,
            [col-start] minmax(min-content, 14rem) [col-end]
        )
        [center-end]
        minmax(6rem, 1fr) [full-end];

    & > * {
    }

    @media (max-width: $bp-largest) {
        grid-template-rows: 8rem 80vh min-content 40vw repeat(3, min-content);
        grid-template-columns:
            [full-start] minmax(6rem, 1fr)
            [center-start] repeat(
                8,
                [col-start] minmax(min-content, 14rem) [col-end]
            )
            [center-end]
            minmax(6rem, 1fr) [full-end];
    }

    @media (max-width: $bp-big) {
        grid-template-rows: 8rem calc(100vh - 8rem);
    }

    @media (max-width: $bp-small) {
    }
}

