    color: white;
    border: none;
    border-radius: 0;
    font-family: $font-display;
    text-transform: uppercase;
    padding: 1.8rem 3rem;
    cursor: pointer;
    transition: all 0.4s;
    outline: none;

    &:hover {
        background-color: $color-primary-dark;
    }
}

