sources.sass/_homes.scss:
.homes {
    grid-column: center-start / center-end;
    margin: 15rem 0;

    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(25rem, 1fr));

    grid-gap: 7rem;

    @media (max-width: $bp-largest) {
        grid-template-columns: repeat(auto-fit, minmax(35rem, 1fr));
    }
}

.home {
    background-color: $color-grey-light-1;

    display: grid;

    grid-template-columns: repeat(2, 1fr);

    grid-row-gap: 3.5rem;

    box-shadow: 0 2rem 4rem rgba(black, 0.2);

    transition: all 1s;

    &__img {
        width: 100%;
        grid-column: 1 / -1;
        grid-row: 1/ 2;
        z-index: 1;
    }

    &__like {
        fill: $color-primary;
        height: 2rem;
        width: 2.5rem;
        grid-row: 1/ 2;
        grid-column: 2/ 3;
        z-index: 2;
        justify-self: end;
        margin: 1rem;
    }

    &__name {
        grid-column: 1 / 3;
        grid-row: 1 / 2;
        z-index: 3;
        align-self: end;
        transform: translateY(50%);

        width: 80%;
        justify-self: center;

        font-family: $font-display;
        font-size: 1.6rem;
        font-weight: 400;
        text-align: center;
        padding: 1.25rem;
        background-color: $color-secondary;
        color: white;
        transition: all 0.4s;

        &:hover {
            background-color: lighten($color-secondary, 7%);
        }
    }

    &__location,
    &__rooms {
        margin-top: 2.5rem;
    }

    &__location,
    &__rooms,
    &__area,
    &__price {
        grid-template-columns: 1fr 2fr;
        font-size: 1.5rem;

        display: grid;

        svg {
            fill: $color-primary;
            height: 2rem;
            width: 2rem;
            justify-self: end;
        }

        p {
            align-self: center;
            margin-left: 0.7rem;
        }
    }

    &__btn {
        grid-column: 1 / 3;
    }
}

.home:hover {
    transform: translateY(-10px);
}

sources.sass/_gallery.scss:
.gallery {
    background-color: $color-secondary;

    grid-column: full-start / full-end;

    display: grid;
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: repeat(7, 5vw);
    grid-gap: 1.5rem;
    padding: 1.5rem;

    &__item {
        box-shadow: 0 -4rem 15rem rgba($color-secondary, 0.3);
        &--1 {
            grid-row: 5 / 8;
            grid-column: 5 / 8;
        }

        &--2 {
            grid-row: 3 / 6;
            grid-column: 1 / 3;
        }

        &--3 {
            grid-row: 4 / 5;
            grid-column: 5 / 6;
        }

        &--4 {
            grid-row: 1 / 4;
            grid-column: 3 / 5;
        }

        &--5 {
            grid-row: 6 / 8;
            grid-column: 1 / 2;
        }

        &--6 {
            grid-row: 6 / 8;
            grid-column: 2 / 4;
        }

        &--7 {
            grid-row: 6 / 8;
            grid-column: 4 / 5;
        }

        &--8 {
            grid-row: 3 / 5;
            grid-column: 6 / 8;
        }

        &--9 {
            grid-row: 1 / 4;
            grid-column: 5 / 6;
        }

        &--10 {
            grid-row: 4 / 6;
            grid-column: 3 / 5;
        }

        &--11 {
            grid-row: 1 / span 2;
            grid-column: 1 / span 2;
        }

        &--12 {
            grid-row: 1 / 3;
            grid-column: 6 / 9;
        }

        &--13 {
            grid-row: 3 / 6;
            grid-column: 8 / 9;
        }

        &--14 {
            grid-row: 6 / 8;
            grid-column: 8 / 9;
        }
    }

    &__img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        display: block;
    }
}

